import express from "express";
import nunjucks from "nunjucks";
import bodyParser from "body-parser";
import session from "express-session";

import { getAllJobRoles } from "./main/controllers/JobRoleController";
import { dateFilter } from "./main/filters/DateFilter";
import { getLoginForm, getLogoutForm, getNotLoggedIn, postLoginForm, postLogoutForm } from "./main/controllers/AuthControllers";
import { getHomePage } from "./main/controllers/HomeController";
import { allowRoles } from "./main/middleware/AuthMiddleware";
import { UserRole } from "./main/models/JwtToken";

const app = express();

nunjucks.configure('views/', {
    autoescape: false,
    express: app
});

const env = nunjucks.configure('views',{
  autoescape: true,
  express: app
});

env.addFilter('date', dateFilter);
app.use(express.static('public'));
app.set('view engine', 'html')

app.use(bodyParser.json())
app.use(bodyParser.urlencoded({
  extended: true
}))

app.use(session({ secret: 'SUPER_SECRET', cookie: { maxAge: 28800000 }}));

declare module "express-session" {
  interface SessionData {
    token: string;
  }
}

app.listen(3000, () => {
  console.log('Server started on port 3000');
});

app.get('/', getHomePage);
app.get('/loginForm', getLoginForm);
app.post('/loginForm', postLoginForm);
app.get('/logoutForm', getLogoutForm);
app.post('/logoutForm', postLogoutForm);
app.get('/notLoggedIn', getNotLoggedIn);
app.get('/notAuthorised', getNotLoggedIn);
app.get('/job-roles', allowRoles([UserRole.Admin, UserRole.User]), getAllJobRoles);

import { LoginRequest } from "../models/LoginRequest";
import { getTokenByloggingIn} from "../services/AuthService";
import express from "express";

export const getLoginForm = async (req: express.Request, res: express.Response): Promise<void> => {
    res.render('pages/loginPage.html', {pageName: 'Login page'});
}

export const postLoginForm  = async (req: express.Request, res: express.Response): Promise<void> => {
    try {
        const loginRequest: LoginRequest = req.body;
        req.session.token = await getTokenByloggingIn(loginRequest);
        res.redirect('/')
    } catch (e) {
        res.locals.errormessage = e.message;
        res.locals.pageName = "Login Page"
        res.render('pages/loginPage.html', req.body);
    }
}

export const getNotLoggedIn = async (req: express.Request, res: express.Response): Promise<void> => {
    res.render('pages/notloggedIn.html', {pageName: 'Not logged in'});
}

export const getLogoutForm = async (req: express.Request, res: express.Response): Promise<void> => {
    res.render('pages/logoutPage.html', {pageName: 'Logout page'});
}

export const postLogoutForm = async(req: express.Request, res: express.Response): Promise<void> => {

    req.session.token = "";
    res.redirect('/')

}

import express from "express";

export const getHomePage = async (req: express.Request, res: express.Response): Promise<void> => {
    res.render('pages/home.html', {pageName: "Kainos Jobs Homepage"});
}

import express from "express";
import { getJobRoles } from "../services/JobRoleService"

export const getAllJobRoles = async (req: express.Request, res: express.Response): Promise<void> => {
    try{
        res.render('pages/allJobRolesList.html', {jobRoles: await getJobRoles(req.session.token), pageName: "Job Roles"});
    }catch (e) {
        res.locals.errormessage = e.message;
        res.locals.pageName = "Job Roles";
        res.render("pages/allJobRolesList.html", res);
    }
}

export const dateFilter = (unixTimestamp: number): string => {
    return new Date(unixTimestamp).toDateString();
}

import express from "express";
import { jwtDecode } from "jwt-decode";
import { JwtToken, UserRole } from "../models/JwtToken";
import "core-js/stable/atob";

export const allowRoles = (allowedRoles: UserRole[]) => {
    return (req: express.Request, res: express.Response, next: express.NextFunction) => {
        if(!req.session.token || req.session.token == ''){
            res.redirect("/notLoggedIn")
            return res.status(401);
        }

        const decodedToken: JwtToken = jwtDecode(req.session.token);
        if(!allowedRoles.includes(decodedToken.Role)){
            res.redirect("/notAuthorised")
            return res.status(403);
        }

        next();
    }
}

export type JobRoleResponse ={
    id: number
    roleName: string;
    location: string;
    capability: string;
    band: string;
    closingDate: Date;
    status: string;
}

export enum UserRole {
    Admin = 1,
    User = 2
}

export type JwtToken = {
    Role: UserRole
}

export type LoginRequest = {
    email: string,
    password: string
}

import axios, { AxiosResponse } from "axios";
import { LoginRequest } from "../models/LoginRequest";

axios.defaults.baseURL = process.env.API_URL || 'http://localhost:8080/';

export const URL: string = "/api/auth/login";

export const getTokenByloggingIn = async (loginRequest: LoginRequest): Promise<string> => {
    try{
        const response: AxiosResponse = await axios.post(URL, loginRequest);

        return response.data;
    } catch (e) {
        if(e.response.status == 404){
            throw new Error("user does not exist.");
        }
        else if(e.response.status == 400){
            throw new Error("user is not valid: Invalid Credentials.");
        }
        else if(e.response.status == 500){
            throw new Error("Internal Server Error.");
        }
        else{
            throw new Error(e.message);
        }
    } 
}

export const getHeader = (token: string) => {
    return{
        headers: { Authorization: `Bearer ${token}`}
    }
}

import axios, { AxiosResponse } from "axios";
import { JobRoleResponse } from "../models/JobRoleResponse";
axios.defaults.baseURL = process.env.API_URL || 'http://localhost:8080/';
import { getHeader } from "../services/AuthUtil";

export const URL: string = "/api/JobRoles";
export const getJobRoles = async (token: string): Promise<JobRoleResponse[]> => {
    try {
        const response: AxiosResponse = await axios.get(URL, getHeader(token))

        return response.data;        
    } catch (e) {
        throw new Error('Failed to get JobRoles')
    }
}
